import React, { useState } from 'react';
import { Box, Typography, Button, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundImage: 'url("https://thumbs.dreamstime.com/b/forest-pixel-art-background-animation-d-pixel-video-game-daytime-green-grass-fir-trees-floating-clouds-forest-pixel-art-301783252.jpg")',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
}));

const WizardImage = styled('img')({
  width: '200px',
  height: 'auto',
  position: 'absolute',
  bottom: '20%',
  left: '50%',
  transform: 'translateX(-50%)',
});

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  bottom: '10%',
  left: '50%',
  transform: 'translateX(-50%)',
  padding: theme.spacing(3),
  backgroundColor: 'rgba(0, 0, 0, 0.8)',
  color: 'white',
  maxWidth: '80%',
  textAlign: 'center',
  borderRadius: '10px',
}));

const NextButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  backgroundColor: '#4CAF50',
  color: 'white',
  '&:hover': {
    backgroundColor: '#45a049',
  },
}));

const messages = [
  "The world of Vocabia is in danger...",
  "The demon king Dysauron stole the scrolls of knowledge.",
  "Villagers are becoming silent and minds are growing dull.",
  "You must defeat the monsters and recover the scrolls."
];

const WizardScene = ({ onComplete }) => {
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);

  const handleNext = () => {
    if (currentMessageIndex < messages.length - 1) {
      setCurrentMessageIndex(prev => prev + 1);
    } else {
      onComplete();
    }
  };

  return (
    <SceneContainer>
      <WizardImage 
        src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRklh4p4HlbA6en98jdtG_fGwr5_UDSNNR4emlcqZegLzlpAJh8BnZUTVhRy-KVeMTNIeI&usqp=CAU" 
        alt="Wizard" 
      />
      <DialogueBox elevation={3}>
        <Typography variant="h6" gutterBottom>
          {messages[currentMessageIndex]}
        </Typography>
        <NextButton
          variant="contained"
          onClick={handleNext}
        >
          {currentMessageIndex < messages.length - 1 ? 'Next' : 'Begin Journey'}
        </NextButton>
      </DialogueBox>
    </SceneContainer>
  );
};

export default WizardScene;
