import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { keyframes } from '@mui/system';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const shake = keyframes`
  0% { transform: translateX(0); }
  25% { transform: translateX(-5px); }
  50% { transform: translateX(5px); }
  75% { transform: translateX(-5px); }
  100% { transform: translateX(0); }
`;

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundImage: 'url("https://imgcdn.stablediffusionweb.com/2024/12/4/6c3aa105-23e7-41bf-b84d-b0bc2624707d.jpg")',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
  animation: `${fadeIn} 2s ease-in`,
  overflow: 'hidden',
}));

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  bottom: '10%',
  left: '50%',
  transform: 'translateX(-50%)',
  padding: theme.spacing(3),
  backgroundColor: 'rgba(0, 0, 0, 0.8)',
  color: 'white',
  maxWidth: '80%',
  textAlign: 'center',
  borderRadius: '10px',
  zIndex: 10,
}));

const BattleButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  backgroundColor: '#f44336',
  color: 'white',
  '&:hover': {
    backgroundColor: '#d32f2f',
  },
}));

const MonsterSprite = styled('img')({
  position: 'absolute',
  right: '15%',
  bottom: '25%',
  width: '200px',
  height: 'auto',
  animation: `${float} 3s ease-in-out infinite`,
  zIndex: 5,
});

const BurningHouse = styled('img')({
  position: 'absolute',
  left: '10%',
  bottom: '20%',
  width: '300px',
  height: 'auto',
  animation: `${shake} 0.5s ease-in-out infinite`,
  zIndex: 4,
});

const VillageScene = ({ onComplete }) => {
  const [showButton, setShowButton] = useState(false);
  const [currentMessage, setCurrentMessage] = useState(0);

  const messages = [
    "The village is under attack! A shadowy figure lurks in the forest...",
    "The monster has set fire to the houses. We must stop them!",
    "Use your knowledge of words to defeat this creature and save the village!"
  ];

  useEffect(() => {
    // Show messages in sequence
    const messageTimer = setInterval(() => {
      setCurrentMessage(prev => {
        if (prev < messages.length - 1) {
          return prev + 1;
        }
        clearInterval(messageTimer);
        return prev;
      });
    }, 3000);

    // Show the battle button after all messages
    const buttonTimer = setTimeout(() => {
      setShowButton(true);
    }, 9000);

    return () => {
      clearInterval(messageTimer);
      clearTimeout(buttonTimer);
    };
  }, []);

  return (
    <SceneContainer>
      <BurningHouse 
        src="https://imgcdn.stablediffusionweb.com/2024/12/4/6c3aa105-23e7-41bf-b84d-b0bc2624707d.jpg" 
        alt="Burning House" 
      />
      <MonsterSprite 
        src="https://www.clipartmax.com/png/middle/328-3285650_3-shadow-monster-enemy-character-sprite-enemy-character.png" 
        alt="Monster" 
      />
      <DialogueBox elevation={3}>
        <Typography variant="h6" gutterBottom>
          {messages[currentMessage]}
        </Typography>
        {showButton && (
          <BattleButton
            variant="contained"
            onClick={onComplete}
          >
            Enter Battle
          </BattleButton>
        )}
      </DialogueBox>
    </SceneContainer>
  );
};

export default VillageScene;
