import React, { useState } from 'react';
import { Box, Typography, Button, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'flex-end',
  backgroundImage: 'url("https://wallpapers.com/images/hd/cartoon-forest-background-1920-x-1080-3si03xbjuob5zkdp.jpg")',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
  overflow: 'hidden',
}));

const SpritesRow = styled(Box)(({ theme }) => ({
  width: '100%',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  justifyContent: 'space-between',
  position: 'absolute',
  bottom: '18%',
  left: 0,
  padding: '0 3vw',
  zIndex: 2,
}));

const WizardSprite = styled('img')({
  width: '110px',
  height: 'auto',
  marginRight: '10px',
});

const UserSprite = styled('img')({
  width: '80px',
  height: 'auto',
  marginLeft: '10px',
});

const MonsterSprite = styled('img')({
  width: '110px',
  height: 'auto',
  marginRight: '30px',
});

const BurningHouse = styled('img')({
  width: '120px',
  height: 'auto',
  marginLeft: '10px',
});

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  bottom: '5%',
  left: '50%',
  transform: 'translateX(-50%)',
  padding: theme.spacing(3),
  backgroundColor: 'rgba(0, 0, 0, 0.85)',
  color: 'white',
  maxWidth: '80%',
  textAlign: 'center',
  borderRadius: '10px',
  zIndex: 10,
  minWidth: '320px',
}));

const BattleButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  backgroundColor: '#f44336',
  color: 'white',
  '&:hover': {
    backgroundColor: '#d32f2f',
  },
}));

const messages = [
  "Wizard: This is terrible! The monster is attacking the village!",
  "You: We have to do something! The house is on fire!",
  "Wizard: Be brave! Use your knowledge to defeat the monster and save the village!"
];

const VillageScene = ({ onComplete }) => {
  const [currentMessage, setCurrentMessage] = useState(0);

  const handleNext = () => {
    if (currentMessage < messages.length - 1) {
      setCurrentMessage(currentMessage + 1);
    }
  };

  return (
    <SceneContainer>
      <SpritesRow>
        <WizardSprite 
          src="https://www.shutterstock.com/image-vector/pixel-art-wizard-long-beard-260nw-2544146115.jpg" 
          alt="Wizard" 
        />
        <UserSprite 
          src="https://w7.pngwing.com/pngs/928/303/png-transparent-pixel-dodgers-pixel-art-sprite-text-cartoon-fictional-character.png" 
          alt="User" 
        />
        <MonsterSprite 
          src="https://www.vhv.rs/dpng/d/357-3572106_stone-monster-2d-game-hd-png-download.png" 
          alt="Monster" 
        />
        <BurningHouse 
          src="https://w7.pngwing.com/pngs/603/536/png-transparent-isolated-modern-house-on-fire-thumbnail.png" 
          alt="Burning House" 
        />
      </SpritesRow>
      <DialogueBox elevation={3}>
        <Typography variant="h6" gutterBottom>
          {messages[currentMessage]}
        </Typography>
        {currentMessage < messages.length - 1 ? (
          <Button variant="outlined" color="primary" onClick={handleNext}>
            Continue
          </Button>
        ) : (
          <BattleButton
            variant="contained"
            onClick={onComplete}
          >
            Enter Battle
          </BattleButton>
        )}
      </DialogueBox>
    </SceneContainer>
  );
};

export default VillageScene;
