import React from "react";
import "../styles/MapView.css";

const islands = [
  { id: 1, name: "Grammar Gorge", type: "jungle", style: { top: "10%", left: "10%" }, unlocked: true },
  { id: 2, name: "Home of Spellisk", type: "shores", style: { top: "10%", right: "10%" }, unlocked: false },
  { id: 3, name: "Home of Fraggmorr", type: "swamp", style: { top: "40%", left: "30%" }, unlocked: false },
  { id: 4, name: "Home of Cloggon", type: "snow", style: { bottom: "10%", left: "10%" }, unlocked: false },
  { id: 5, name: "Home of Dysauron the final boss", type: "volcano", style: { bottom: "10%", right: "10%" }, unlocked: false },
];

export default function MapView() {
  const handleIslandClick = (island) => {
    if (island.unlocked) {
      // Navigate to island
      alert(`Navigating to ${island.name}!`);
    }
  };

  return (
    <div className="map-view">
      {islands.map((island) => (
        <div
          key={island.id}
          className={`island island-${island.type}${island.unlocked ? "" : " locked"}`}
          style={island.style}
          onClick={() => handleIslandClick(island)}
        >
          {island.name}
          {!island.unlocked && <span className="lock-icon">ðŸ”’</span>}
          {!island.unlocked && <span className="tooltip">Complete previous islands to unlock!</span>}
        </div>
      ))}
    </div>
  );
}
