import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Dialog,
  DialogContent,
  DialogActions,
  Paper,
  Grid,
} from '@mui/material';
import { styled, keyframes } from '@mui/material/styles';

const JUNGLE_BG = 'https://d2gg9evh47fn9z.cloudfront.net/1600px_COLOURBOX37132213.jpg';
const WIZARD_IMG = 'https://www.shutterstock.com/image-vector/pixel-art-wizard-long-beard-260nw-2544146115.jpg';
const USER_IMG = 'https://w7.pngwing.com/pngs/928/303/png-transparent-pixel-dodgers-pixel-art-sprite-text-cartoon-fictional-character.png';
const COMMAWIDOW_IMG = 'https://img.freepik.com/premium-photo/3d-pixel-art-scary-black-spider-with-white-fang-halloween-decorative-ornament-theme-design_477250-292.jpg';

const bounce = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'flex-end',
  backgroundImage: `url(${JUNGLE_BG})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
  overflow: 'hidden',
}));

const SpritesRow = styled(Box)(({ theme }) => ({
  width: '900px',
  maxWidth: '90vw',
  position: 'absolute',
  left: '50%',
  transform: 'translateX(-50%)',
  bottom: '130px',
  height: '160px',
  zIndex: 3,
  pointerEvents: 'none',
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'space-between',
}));

const WizardUserGroup = styled(Box)(({ theme }) => ({
  position: 'absolute',
  left: 0,
  bottom: 0,
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  gap: '24px',
}));

const CommawidowGroup = styled(Box)(({ theme }) => ({
  position: 'absolute',
  right: 0,
  bottom: 0,
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  gap: '32px',
}));

const WizardSprite = styled('img')({
  width: '120px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2.2s infinite`,
});

const UserSprite = styled('img')({
  width: '100px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2s infinite`,
});

const CommawidowSprite = styled('img')({
  width: '140px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2.3s infinite`,
});

const FLESH_BROWN = '#e6c7b2';
const NAME_BG = '#d1a97a';

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  left: '50%',
  bottom: 0,
  transform: 'translateX(-50%)',
  width: '900px',
  maxWidth: '90vw',
  minWidth: '320px',
  padding: theme.spacing(3, 4),
  backgroundColor: FLESH_BROWN,
  color: '#3a2a1a',
  textAlign: 'left',
  borderRadius: '18px 18px 0 0',
  zIndex: 10,
  boxShadow: '0 -2px 16px 2px rgba(0,0,0,0.18)',
  border: '3px solid #b48a6e',
  fontFamily: 'monospace',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  boxSizing: 'border-box',
}));

const NameTag = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: '-22px',
  left: '18px',
  background: NAME_BG,
  color: '#3a2a1a',
  borderRadius: '16px',
  padding: '4px 18px',
  fontWeight: 700,
  fontSize: '1.1rem',
  boxShadow: '0 2px 8px #b48a6e44',
  border: '2px solid #b48a6e',
  fontFamily: 'monospace',
  zIndex: 2,
}));

const ClickPrompt = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: '16px',
  right: '32px',
  color: '#00996b',
  fontSize: '1.1rem',
  fontWeight: 700,
  opacity: 0.85,
  pointerEvents: 'none',
  zIndex: 5,
  fontFamily: 'monospace',
}));

const HeartRow = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  gap: '8px',
  position: 'absolute',
  top: '24px',
  left: '32px',
  zIndex: 20,
}));

const TimerBox = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: '24px',
  right: '32px',
  background: '#fffbe6',
  color: '#3a2a1a',
  borderRadius: '12px',
  padding: '6px 18px',
  fontWeight: 700,
  fontFamily: 'monospace',
  fontSize: '1.2rem',
  boxShadow: '0 2px 8px #b48a6e44',
  border: '2px solid #b48a6e',
  zIndex: 20,
}));

const dialogueSequence = [
  { speaker: "Wizard", text: "This is it... the jungle where Grammowl's corruption began." },
  { speaker: "User", text: "So this is where the first scroll was stolen?" },
  { speaker: "Wizard", text: "Yes. The Scroll of Grammar lies deep within Grammowl's tower. But the path is twisted with beasts that warp language into nonsense." },
  { speaker: "Grammowl", text: "Foolish fledgling... You flutter into my grove thinking you can rewrite fate?" },
  { speaker: "User", text: "Grammowl!" },
  { speaker: "Grammowl", text: "The Scroll belongs to me now. Without it, the villagers' minds will stay broken... just how I like them." },
  { speaker: "Wizard", text: "Grammowl, your reign of confusion ends here! Chosen one, prepare yourself!" },
  { speaker: "Grammowl", text: "You'll have to get through my little pets first… if your tiny brain can handle proper punctuation!" },
  { speaker: "Commawidow", text: "Welcome to my web, sweet reader. One misplaced pause… and you'll be tangled for eternity!" },
  { speaker: "User", text: "I'm not afraid of your tricks!" },
  { speaker: "Commawidow", text: "Then let's play a little game… Answer wrong, and you'll feel the sting of silence!" },
];

const questions = [
  {
    question: "Which sentence uses commas correctly in a list?",
    options: [
      "I packed my books pencils and erasers.",
      "I packed my books, pencils and erasers.",
      "I packed my books, pencils, and erasers."
    ],
    correctAnswer: 2
  },
  {
    question: "Which greeting is correctly punctuated?",
    options: [
      "Hello John.",
      "Hello, John.",
      "Hello John,"
    ],
    correctAnswer: 1
  },
  {
    question: "Choose the sentence with correct punctuation.",
    options: [
      "I wanted to play but it started raining.",
      "I wanted to play, but it started raining.",
      "I wanted to play but, it started raining."
    ],
    correctAnswer: 1
  },
  {
    question: "Which sentence uses a comma correctly?",
    options: [
      "After dinner we went for a walk.",
      "After dinner, we went for a walk.",
      "After, dinner we went for a walk."
    ],
    correctAnswer: 1
  },
  {
    question: "Which sentence uses commas correctly?",
    options: [
      "It was a cold dark night.",
      "It was a cold, dark night.",
      "It was a cold dark, night."
    ],
    correctAnswer: 1
  }
];

const parseMessage = (msg) => {
  const idx = msg.indexOf(':');
  if (idx !== -1) {
    return [msg.slice(0, idx), msg.slice(idx + 1).trim()];
  }
  return ["", msg];
};

const CommawidowLevel = () => {
  const navigate = useNavigate();
  const [currentDialogue, setCurrentDialogue] = useState(0);
  const [showQuiz, setShowQuiz] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [health, setHealth] = useState(100);
  const [timeLeft, setTimeLeft] = useState(60);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [victory, setVictory] = useState(false);
  const [showClickPrompt, setShowClickPrompt] = useState(false);
  const idleTimeout = React.useRef(null);

  // Show Commawidow sprite only when she is speaking
  const showCommawidow = !showQuiz && dialogueSequence[currentDialogue].speaker === 'Commawidow';
  // Hide left sprites for Grammowl (off-screen)
  const showLeftSprites = !showQuiz && dialogueSequence[currentDialogue].speaker !== 'Grammowl';

  // Dialogue click/idle logic
  React.useEffect(() => {
    setShowClickPrompt(false);
    if (idleTimeout.current) clearTimeout(idleTimeout.current);
    if (!showQuiz) {
      idleTimeout.current = setTimeout(() => setShowClickPrompt(true), 2000);
    }
    return () => clearTimeout(idleTimeout.current);
  }, [currentDialogue, showQuiz]);

  useEffect(() => {
    if (timeLeft > 0 && !gameOver && !victory) {
      const timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else if (timeLeft === 0) {
      setGameOver(true);
    }
  }, [timeLeft, gameOver, victory]);

  const handleAnswer = (index) => {
    setSelectedAnswer(index);
    setShowResult(true);

    if (index === questions[currentQuestion].correctAnswer) {
      if (currentQuestion === questions.length - 1) {
        setVictory(true);
      } else {
        setTimeout(() => {
          setCurrentQuestion(prev => prev + 1);
          setSelectedAnswer(null);
          setShowResult(false);
        }, 1500);
      }
    } else {
      setHealth(prev => Math.max(0, prev - 20));
      if (health <= 20) {
        setGameOver(true);
      }
    }
  };

  const handleContinue = () => {
    if (victory) {
      navigate('/jungle-lush');
    } else if (gameOver) {
      navigate('/jungle-lush');
    }
  };

  const handleDialogueClick = () => {
    setShowClickPrompt(false);
    if (idleTimeout.current) clearTimeout(idleTimeout.current);
    if (currentDialogue < dialogueSequence.length - 1) {
      setCurrentDialogue(currentDialogue + 1);
    } else {
      setShowQuiz(true);
    }
  };

  return (
    <SceneContainer>
      <SpritesRow>
        {showLeftSprites && (
          <WizardUserGroup>
            <WizardSprite src={WIZARD_IMG} />
            <UserSprite src={USER_IMG} />
          </WizardUserGroup>
        )}
        <CommawidowGroup>
          {showCommawidow && (
            <CommawidowSprite src={COMMAWIDOW_IMG} />
          )}
        </CommawidowGroup>
      </SpritesRow>
      {!showQuiz ? (
        <DialogueBox elevation={6} onClick={handleDialogueClick} style={{ cursor: 'pointer', userSelect: 'none' }}>
          <NameTag>{dialogueSequence[currentDialogue].speaker}</NameTag>
          <Typography variant="body1" sx={{ mt: 3, fontSize: '1.2rem', color: '#3a2a13' }}>
            {dialogueSequence[currentDialogue].text}
          </Typography>
          {showClickPrompt && (
            <Typography variant="caption" sx={{ position: 'absolute', right: 24, bottom: 8, color: '#a88b5a' }}>
              Click to continue
            </Typography>
          )}
        </DialogueBox>
      ) : (
        <DialogueBox elevation={6}>
          <NameTag>Commawidow</NameTag>
          <Typography variant="h5" gutterBottom sx={{ color: '#3a2a13', mt: 3 }}>
            {questions[currentQuestion].question}
          </Typography>
          <Grid container spacing={2}>
            {questions[currentQuestion].options.map((option, index) => (
              <Grid item xs={12} key={index}>
                <Button
                  variant="contained"
                  onClick={() => handleAnswer(index)}
                  disabled={showResult}
                  selected={selectedAnswer === index}
                  correct={index === questions[currentQuestion].correctAnswer}
                >
                  {option}
                </Button>
              </Grid>
            ))}
          </Grid>
          <Dialog open={gameOver || victory} onClose={handleContinue}>
            <DialogContent>
              <Typography variant="h5" align="center">
                {victory ? "Victory! You've defeated Commawidow!" : "Game Over! Try again!"}
              </Typography>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleContinue} color="primary">
                Continue
              </Button>
            </DialogActions>
          </Dialog>
        </DialogueBox>
      )}
    </SceneContainer>
  );
};

export default CommawidowLevel;
