import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Grid } from '@mui/material';
import { styled, keyframes } from '@mui/material/styles';

const forestBg = "https://wallpapers.com/images/hd/cartoon-forest-background-1920-x-1080-3si03xbjuob5zkdp.jpg";
const userSprite = "https://w7.pngwing.com/pngs/928/303/png-transparent-pixel-dodgers-pixel-art-sprite-text-cartoon-fictional-character.png";
const monsterSprite = "https://www.vhv.rs/dpng/d/357-3572106_stone-monster-2d-game-hd-png-download.png";
const burningHouse = "https://w7.pngwing.com/pngs/603/536/png-transparent-isolated-modern-house-on-fire-thumbnail.png";
const heartIcon = "https://p7.hiclipart.com/preview/28/266/352/pixel-art-heart-8-bit-color-heart-thumbnail.jpg";
const clockIcon = "https://cdn-icons-png.flaticon.com/512/61/61112.png";

const bounce = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const BattleContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  position: 'relative',
  backgroundImage: `url(${forestBg})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  overflow: 'hidden',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-end',
}));

const TopBar = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'flex-start',
  padding: '14px 24px 0 24px',
  zIndex: 10,
}));

const RightBar = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  gap: '24px',
  minWidth: '260px',
  justifyContent: 'flex-end',
}));

const HeartsRow = styled(Box)(({ theme }) => ({
  display: 'flex',
  gap: '8px',
}));

const HeartImg = styled('img')({
  width: '40px',
  height: '40px',
});

const TimerBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  gap: '2px',
}));

const ClockImg = styled('img')({
  width: '32px',
  height: '32px',
});

const Ground = styled(Box)(({ theme }) => ({
  position: 'absolute',
  left: 0,
  bottom: '230px',
  width: '100%',
  height: '40px',
  background: 'linear-gradient(to top, #3e2e1a 80%, rgba(62,46,26,0.2) 100%)',
  zIndex: 10,
  borderTopLeftRadius: '30px',
  borderTopRightRadius: '30px',
  boxShadow: '0 0 16px 2px rgba(0,0,0,0.25)',
}));

const SpritesRow = styled(Box)(({ theme }) => ({
  width: '100%',
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  justifyContent: 'center',
  position: 'absolute',
  bottom: '270px',
  left: 0,
  zIndex: 20,
  pointerEvents: 'none',
  padding: '0 4vw',
  minHeight: '220px',
}));

const UserSprite = styled('img')({
  width: '170px',
  height: 'auto',
  marginRight: '60px',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2s infinite`,
  zIndex: 21,
});

const MonsterSprite = styled('img')({
  width: '190px',
  height: 'auto',
  marginLeft: '60px',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  transform: 'scaleX(-1)',
  animation: `${bounce} 2.3s infinite`,
  zIndex: 21,
});

const VS = styled(Box)(({ theme }) => ({
  fontSize: '2.5rem',
  fontWeight: 900,
  color: '#fff',
  textShadow: '0 2px 8px #000',
  margin: '0 18px',
  zIndex: 5,
  pointerEvents: 'none',
}));

const BurningHouse = styled('img')({
  width: '110px',
  height: 'auto',
  position: 'absolute',
  right: '4vw',
  bottom: '290px',
  zIndex: 19,
  opacity: 0.85,
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.3))',
});

const BottomBar = styled(Box)(({ theme }) => ({
  position: 'fixed',
  left: 0,
  bottom: 0,
  width: '100vw',
  background: 'rgba(122,62,46,0.97)',
  minHeight: '100px',
  padding: '18px 0 18px 0',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  zIndex: 30,
  borderTopLeftRadius: '32px',
  borderTopRightRadius: '32px',
  boxShadow: '0 -2px 24px 2px rgba(0,0,0,0.32)',
}));

const QuestionText = styled(Typography)(({ theme }) => ({
  color: 'white',
  fontWeight: 600,
  fontSize: '1.35rem',
  marginBottom: '22px',
  textAlign: 'center',
  textShadow: '0 2px 8px #000',
}));

const AnswerButton = styled(Button)(({ theme }) => ({
  width: '340px',
  margin: '10px 0',
  backgroundColor: '#00e6b8',
  color: '#7a3e2e',
  fontWeight: 700,
  fontSize: '1.15rem',
  borderRadius: '32px',
  boxShadow: '0 2px 8px 0 rgba(0,0,0,0.10)',
  '&:hover': {
    backgroundColor: '#00bfae',
  },
}));

const tutorialQuestions = [
  {
    question: "What is the correct form of the verb in this sentence: 'She ___ to the store yesterday.'",
    options: ["go", "goes", "went", "going"],
    correctAnswer: "went"
  },
  {
    question: "Which word is a proper noun?",
    options: ["city", "London", "building", "street"],
    correctAnswer: "London"
  },
  {
    question: "What is the past tense of 'write'?",
    options: ["wrote", "written", "writed", "writing"],
    correctAnswer: "wrote"
  },
  {
    question: "Which sentence uses correct punctuation?",
    options: [
      "The cat, dog and bird are pets.",
      "The cat, dog, and bird are pets.",
      "The cat dog and bird are pets.",
      "The cat; dog; and bird are pets."
    ],
    correctAnswer: "The cat, dog, and bird are pets."
  },
  {
    question: "What is the opposite of 'begin'?",
    options: ["start", "continue", "end", "pause"],
    correctAnswer: "end"
  }
];

const TutorialBattle = ({ onComplete }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [hearts, setHearts] = useState(3);
  const [timeLeft, setTimeLeft] = useState(60);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    if (timeLeft > 0 && !gameOver) {
      const timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else if (timeLeft === 0 && !gameOver) {
      handleWrongAnswer();
    }
  }, [timeLeft, gameOver]);

  const handleAnswer = (answer) => {
    if (answer === tutorialQuestions[currentQuestion].correctAnswer) {
      setScore(prev => prev + 1);
      if (currentQuestion < tutorialQuestions.length - 1) {
        setCurrentQuestion(prev => prev + 1);
        setTimeLeft(60);
      } else {
        endGame();
      }
    } else {
      handleWrongAnswer();
    }
  };

  const handleWrongAnswer = () => {
    setHearts(prev => prev - 1);
    if (hearts <= 1) {
      endGame();
    } else {
      setTimeLeft(60);
    }
  };

  const endGame = () => {
    setGameOver(true);
    const stars = Math.ceil((score / tutorialQuestions.length) * 3);
    onComplete({
      stars,
      hearts,
      score,
      totalQuestions: tutorialQuestions.length
    });
  };

  if (gameOver) {
    return null;
  }

  return (
    <BattleContainer>
      <TopBar>
        <HeartsRow>
          {[...Array(3)].map((_, idx) => (
            <HeartImg key={idx} src={heartIcon} alt="Heart" style={{ opacity: idx < hearts ? 1 : 0.3 }} />
          ))}
        </HeartsRow>
        <RightBar>
          <TimerBox>
            <ClockImg src={clockIcon} alt="Clock" />
            <Typography style={{ color: '#222', fontWeight: 700, fontSize: '1.1rem', marginTop: 2 }}>TIMER</Typography>
            <Typography style={{ color: '#222', fontWeight: 700, fontSize: '1.1rem' }}>{timeLeft}s</Typography>
          </TimerBox>
        </RightBar>
      </TopBar>
      <Ground />
      <SpritesRow>
        <UserSprite src={userSprite} alt="User" />
        <VS>VS</VS>
        <MonsterSprite src={monsterSprite} alt="Monster" />
      </SpritesRow>
      <BurningHouse src={burningHouse} alt="Burning House" />
      <BottomBar>
        <QuestionText>
          {tutorialQuestions[currentQuestion].question}
        </QuestionText>
        <Grid container direction="column" alignItems="center" justifyContent="center">
          {tutorialQuestions[currentQuestion].options.map((option, idx) => (
            <Grid item key={idx}>
              <AnswerButton
                variant="contained"
                onClick={() => handleAnswer(option)}
              >
                {option}
              </AnswerButton>
            </Grid>
          ))}
        </Grid>
      </BottomBar>
    </BattleContainer>
  );
};

export default TutorialBattle;
