import React, { useState, useRef, useEffect } from 'react';
import { Box, Typography, Button, Paper, Grid, CircularProgress, Dialog, DialogActions, DialogContent } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { styled, keyframes } from '@mui/material/styles';
import '../../styles/MapView.css';
import CloseIcon from '@mui/icons-material/Close';
import axios from 'axios';

const JUNGLE_BG = 'https://png.pngtree.com/background/20220727/original/pngtree-jungle-game-background-arcade-art-picture-image_1829537.jpg';
const WIZARD_IMG = 'https://www.shutterstock.com/image-vector/pixel-art-wizard-long-beard-260nw-2544146115.jpg';
const USER_IMG = 'https://w7.pngwing.com/pngs/928/303/png-transparent-pixel-dodgers-pixel-art-sprite-text-cartoon-fictional-character.png';
const COMMAWIDOW_IMG = 'https://static.vecteezy.com/system/resources/previews/027/517/582/non_2x/pixel-cartoon-swamp-monster-png.png';
const TENSAPHANT_IMG = 'https://static.vecteezy.com/system/resources/previews/027/517/582/non_2x/pixel-cartoon-swamp-monster-png.png';
const PLURIBOG_IMG = 'https://cdna.artstation.com/p/assets/images/images/072/803/668/large/blade-blackwood-frogmonster.jpg?1708254629';
const HEART_IMG = 'https://p7.hiclipart.com/preview/28/266/352/pixel-art-heart-8-bit-color-heart-thumbnail.jpg';

const bounce = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const shake = keyframes`
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-8px); }
  75% { transform: translateX(8px); }
`;

const hit = keyframes`
  0% { transform: scale(1); }
  25% { transform: scale(1.1); }
  50% { transform: scale(0.9); }
  75% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const userFlash = keyframes`
  0%, 100% { opacity: 1; filter: brightness(1); }
  50% { opacity: 0.7; filter: brightness(1.2) sepia(1) saturate(10000%) hue-rotate(-50deg); }
`;

const monsterFlash = keyframes`
  0%, 100% { opacity: 1; filter: brightness(1); }
  50% { opacity: 0.7; filter: brightness(1.2) sepia(1) saturate(10000%) hue-rotate(180deg); }
`;

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100vh',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'flex-end',
  backgroundImage: `url(${JUNGLE_BG})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
  overflow: 'hidden',
}));

const Ground = styled(Box)(({ theme }) => ({
  position: 'absolute',
  left: 0,
  bottom: '90px',
  width: '100%',
  height: '40px',
  background: 'linear-gradient(to top, #3e2e1a 80%, rgba(62,46,26,0.2) 100%)',
  zIndex: 2,
  borderTopLeftRadius: '30px',
  borderTopRightRadius: '30px',
  boxShadow: '0 0 16px 2px rgba(0,0,0,0.25)',
}));

const SpritesRow = styled(Box)(({ theme }) => ({
  width: '900px',
  maxWidth: '90vw',
  position: 'absolute',
  left: '50%',
  transform: 'translateX(-50%)',
  bottom: '130px',
  height: '160px',
  zIndex: 3,
  pointerEvents: 'none',
}));

const WizardUserGroup = styled(Box)(({ theme }) => ({
  position: 'absolute',
  left: 0,
  bottom: 0,
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  gap: '24px',
}));

const MonsterGroup = styled(Box)(({ theme }) => ({
  position: 'absolute',
  right: 0,
  bottom: 0,
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'flex-end',
  gap: '32px',
}));

const WizardSprite = styled('img')({
  width: '120px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2.2s infinite`,
});

const UserSprite = styled('img')(({ isDamaged }) => ({
  width: '100px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2s infinite, ${isDamaged ? `${shake} 0.5s ease-in-out, ${userFlash} 0.5s ease-in-out` : 'none'}`,
}));

const MonsterSprite = styled('img')(({ isDamaged }) => ({
  width: '140px',
  height: 'auto',
  filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.5))',
  animation: `${bounce} 2.3s infinite, ${isDamaged ? `${hit} 0.5s ease-in-out, ${monsterFlash} 0.5s ease-in-out` : 'none'}`,
}));

const FLESH_BROWN = '#e6c7b2';
const NAME_BG = '#d1a97a';

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  left: '50%',
  bottom: 0,
  transform: 'translateX(-50%)',
  width: '900px',
  maxWidth: '90vw',
  minWidth: '320px',
  padding: theme.spacing(3, 4),
  backgroundColor: FLESH_BROWN,
  color: '#3a2a1a',
  textAlign: 'left',
  borderRadius: '18px 18px 0 0',
  zIndex: 10,
  boxShadow: '0 -2px 16px 2px rgba(0,0,0,0.18)',
  border: '3px solid #b48a6e',
  fontFamily: 'monospace',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  boxSizing: 'border-box',
}));

const NameTag = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: '-22px',
  left: '18px',
  background: NAME_BG,
  color: '#3a2a1a',
  borderRadius: '16px',
  padding: '4px 18px',
  fontWeight: 700,
  fontSize: '1.1rem',
  boxShadow: '0 2px 8px #b48a6e44',
  border: '2px solid #b48a6e',
  fontFamily: 'monospace',
  zIndex: 2,
}));

const ClickPrompt = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: '16px',
  right: '32px',
  color: '#00996b',
  fontSize: '1.1rem',
  fontWeight: 700,
  opacity: 0.85,
  pointerEvents: 'none',
  zIndex: 5,
  fontFamily: 'monospace',
}));

const HeartRow = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  gap: '8px',
  position: 'absolute',
  top: '24px',
  left: '32px',
  zIndex: 20,
}));

const TimerBox = styled(Box)(({ theme }) => ({
  background: '#fffbe6',
  color: '#3a2a1a',
  borderRadius: '12px',
  padding: '6px 18px',
  fontWeight: 700,
  fontFamily: 'monospace',
  fontSize: '1.2rem',
  boxShadow: '0 2px 8px #b48a6e44',
  border: '2px solid #b48a6e',
  zIndex: 20,
  marginLeft: 16,
}));

const VS = styled(Box)(({ theme }) => ({
  fontSize: '2.5rem',
  fontWeight: 900,
  color: '#fff',
  textShadow: '0 2px 8px #000',
  margin: '0 18px',
  zIndex: 22,
  pointerEvents: 'none',
}));

const TopBar = styled(Box)(({ theme }) => ({
  width: '100%',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  position: 'absolute',
  top: 0,
  left: 0,
  padding: theme.spacing(2),
  zIndex: 20,
}));

const QuestionBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  left: '50%',
  top: '50%',
  transform: 'translate(-50%, -50%)',
  width: '600px',
  maxWidth: '90vw',
  padding: theme.spacing(4),
  backgroundColor: '#fffbe6',
  color: '#3a2a1a',
  textAlign: 'center',
  borderRadius: '18px',
  zIndex: 30,
  boxShadow: '0 4px 24px rgba(0,0,0,0.2)',
  border: '3px solid #b48a6e',
  fontFamily: 'monospace',
}));

const OptionButton = styled(Button)(({ theme, isCorrect, isWrong }) => ({
  width: '100%',
  padding: theme.spacing(2),
  marginTop: theme.spacing(2),
  backgroundColor: isCorrect ? '#4caf50' : isWrong ? '#f44336' : '#fffbe6',
  color: isCorrect || isWrong ? '#fff' : '#3a2a1a',
  border: '2px solid #b48a6e',
  borderRadius: '12px',
  fontFamily: 'monospace',
  fontSize: '1.1rem',
  textTransform: 'none',
  '&:hover': {
    backgroundColor: isCorrect ? '#4caf50' : isWrong ? '#f44336' : '#f5f0e0',
  },
}));

const VictoryDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialog-paper': {
    backgroundColor: '#fffbe6',
    borderRadius: '18px',
    border: '3px solid #b48a6e',
    padding: theme.spacing(3),
    fontFamily: 'monospace',
  },
}));

const StarRow = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(2),
  marginTop: theme.spacing(2),
}));

const Star = styled(Box)(({ theme, filled }) => ({
  fontSize: '2.5rem',
  color: filled ? '#ffd700' : '#d3d3d3',
  textShadow: filled ? '0 0 8px rgba(255,215,0,0.5)' : 'none',
}));

const JungleLushLevel4 = () => {
  const navigate = useNavigate();
  const [currentScene, setCurrentScene] = useState(0);
  const [currentMonster, setCurrentMonster] = useState(0);
  const [hearts, setHearts] = useState(3);
  const [monsterHearts, setMonsterHearts] = useState(3);
  const [showQuestion, setShowQuestion] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showVictory, setShowVictory] = useState(false);
  const [stars, setStars] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [isDamaged, setIsDamaged] = useState(false);
  const [isMonsterDamaged, setIsMonsterDamaged] = useState(false);

  const dialogue = [
    // Scene 1: Arrival at Tower
    { speaker: "Wizard", text: "There it is… the Tower of Grammowl. The Scroll of Grammar pulses within, waiting for its rightful bearer." },
    { speaker: "Wizard", text: "You've fought well, adventurer. One final step remains before you reclaim the first spark of knowledge." },
    { speaker: "Adventurer", text: "After everything we've faced, I'm ready." },
    { speaker: "Wizard", text: "No… something's wrong." },
    { speaker: "System", text: "*TREMBLE*" },
    
    // Scene 2: Underlings Return
    { speaker: "Commawidow", text: "Surprised? You shouldn't be. You only brushed against my rules. Grammar isn't so easily untangled." },
    { speaker: "Tensaphant", text: "You dared disturb the timeline… and now you think you can rewrite it? I am the master of tense!" },
    { speaker: "Pluribog", text: "You thought me defeated? I am the pit itself! Every misused plural… every confused child… I feed on their ignorance!" },
    { speaker: "Adventurer", text: "You're still clinging to your broken rules? I already bested each of you." },
    { speaker: "Commawidow", text: "You escaped our traps… but today, your grammar crumbles!" },
    { speaker: "Tensaphant", text: "We return not in weakness… but in wrath." },
    { speaker: "Pluribog", text: "Your minds may have grown… but we evolved in fury." },
    { speaker: "Wizard", text: "Adventurer, steel your mind and your heart. This is no test… this is a reckoning." },
  ];

  const questions = [
    // Commawidow Questions
    {
      question: "Which of these sentences uses commas correctly?",
      options: [
        "Before leaving she fed the dog, locked the door and grabbed her keys.",
        "Before leaving, she fed the dog, locked the door, and grabbed her keys.",
        "Before leaving she fed, the dog locked, the door, and grabbed her keys."
      ],
      correct: 1
    },
    {
      question: "Select the sentence with the correct greeting and comma usage:",
      options: [
        "Dear Sarah, how are you?",
        "Dear, Sarah how are you?",
        "Dear Sarah how are you,?"
      ],
      correct: 0
    },
    {
      question: "Choose the sentence with the correct use of commas in a compound sentence:",
      options: [
        "I wanted to go hiking but, it started raining.",
        "I wanted to go hiking, but it started raining.",
        "I wanted to go, hiking but it started raining."
      ],
      correct: 1
    },
    // Tensaphant Questions
    {
      question: "Choose the sentence with correct verb tense consistency:",
      options: [
        "He studies hard and passed all his exams.",
        "He studied hard and passed all his exams.",
        "He was study hard and passes all his exams."
      ],
      correct: 1
    },
    {
      question: "Which sentence properly uses future perfect tense?",
      options: [
        "By next week, I will have finished the project.",
        "By next week, I finished the project.",
        "By next week, I will finish the project."
      ],
      correct: 0
    },
    {
      question: "Which sentence maintains proper tense throughout?",
      options: [
        "She will attend the meeting and presented her report.",
        "She attended the meeting and presents her report.",
        "She will attend the meeting and will present her report."
      ],
      correct: 2
    },
    // Pluribog Questions
    {
      question: "Which is the correct sentence?",
      options: [
        "The alumni was gathered for the reunion.",
        "The alumnis were gathered for the reunion.",
        "The alumni were gathered for the reunion."
      ],
      correct: 2
    },
    {
      question: "Identify the sentence with correct plural form:",
      options: [
        "The data is inaccurate and needs reviewing.",
        "The data are inaccurate and need reviewing.",
        "The datas are inaccurate and needs review."
      ],
      correct: 1
    },
    {
      question: "Which sentence correctly uses both singular and plural forms?",
      options: [
        "The criterias of this contest is strict.",
        "The criterion of this contest is strict.",
        "The criterion of this contests are strict."
      ],
      correct: 1
    }
  ];

  const monsterDefeatDialogue = [
    "Grammowl… will punctuate your failure…",
    "You… can't outrun… what's written…",
    "He waits… at the summit… and you are not ready…"
  ];

  const victoryDialogue = [
    { speaker: "Adventurer", text: "They're gone… for good this time." },
    { speaker: "Wizard", text: "They were pawns of a greater madness. Ahead lies the one who twisted their purpose—prepare for Grammowl." }
  ];

  const handleDialogueClick = () => {
    if (showQuestion) return;
    
    if (currentScene < dialogue.length) {
      setCurrentScene(prev => prev + 1);
    } else if (currentMonster < 3) {
      setShowQuestion(true);
    } else {
      setShowVictory(true);
    }
  };

  const handleAnswer = (idx) => {
    const currentQuestionIndex = currentMonster * 3 + currentQuestion;
    const isCorrect = idx === questions[currentQuestionIndex].correct;

    if (isCorrect) {
      setMonsterHearts(prev => prev - 1);
      setIsMonsterDamaged(true);
      setTimeout(() => setIsMonsterDamaged(false), 500);
    } else {
      setHearts(prev => prev - 1);
      setIsDamaged(true);
      setTimeout(() => setIsDamaged(false), 500);
    }

    if (monsterHearts <= 1 || hearts <= 1) {
      if (monsterHearts <= 1) {
        setCurrentMonster(prev => prev + 1);
        setMonsterHearts(3);
        setCurrentQuestion(0);
        setShowQuestion(false);
        setCurrentScene(prev => prev + 1);
      } else {
        // Game over logic
        navigate('/jungle-lush');
      }
    } else {
      setCurrentQuestion(prev => (prev + 1) % 3);
    }
  };

  const handleRetryBattle = () => {
    setHearts(3);
    setMonsterHearts(3);
    setCurrentQuestion(0);
    setShowQuestion(false);
  };

  const handleRetryWholeLevel = () => {
    setCurrentScene(0);
    setCurrentMonster(0);
    setHearts(3);
    setMonsterHearts(3);
    setCurrentQuestion(0);
    setShowQuestion(false);
    setShowVictory(false);
    setStars(0);
  };

  const handleVictory = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('/api/progress/levels/4', {
        stars: Math.max(stars, Math.ceil((hearts / 3) * 3))
      });
      if (response.data.success) {
        navigate('/jungle-lush');
      }
    } catch (error) {
      console.error('Error saving progress:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const getCurrentMonsterImage = () => {
    switch (currentMonster) {
      case 0: return COMMAWIDOW_IMG;
      case 1: return TENSAPHANT_IMG;
      case 2: return PLURIBOG_IMG;
      default: return COMMAWIDOW_IMG;
    }
  };

  const getCurrentMonsterName = () => {
    switch (currentMonster) {
      case 0: return "Commawidow";
      case 1: return "Tensaphant";
      case 2: return "Pluribog";
      default: return "";
    }
  };

  return (
    <SceneContainer onClick={handleDialogueClick}>
      <TopBar>
        <HeartRow>
          {[...Array(3)].map((_, i) => (
            <img
              key={i}
              src={HEART_IMG}
              alt="heart"
              style={{
                width: '32px',
                height: '32px',
                opacity: i < hearts ? 1 : 0.3
              }}
            />
          ))}
          <TimerBox>
            {getCurrentMonsterName()}
          </TimerBox>
        </HeartRow>
        <HeartRow>
          <TimerBox>
            {getCurrentMonsterName()}
          </TimerBox>
          {[...Array(3)].map((_, i) => (
            <img
              key={i}
              src={HEART_IMG}
              alt="heart"
              style={{
                width: '32px',
                height: '32px',
                opacity: i < monsterHearts ? 1 : 0.3
              }}
            />
          ))}
        </HeartRow>
      </TopBar>

      <Ground />
      
      <SpritesRow>
        <WizardUserGroup>
          <WizardSprite src={WIZARD_IMG} alt="Wizard" />
          <UserSprite src={USER_IMG} alt="User" isDamaged={isDamaged} />
        </WizardUserGroup>
        <VS>VS</VS>
        <MonsterGroup>
          <MonsterSprite
            src={getCurrentMonsterImage()}
            alt={getCurrentMonsterName()}
            isDamaged={isMonsterDamaged}
          />
        </MonsterGroup>
      </SpritesRow>

      {!showQuestion && !showVictory && (
        <DialogueBox>
          <NameTag>
            {dialogue[currentScene]?.speaker || "System"}
          </NameTag>
          <Typography variant="body1" sx={{ mb: 2 }}>
            {dialogue[currentScene]?.text || ""}
          </Typography>
          <ClickPrompt>Click to continue...</ClickPrompt>
        </DialogueBox>
      )}

      {showQuestion && !showVictory && (
        <QuestionBox>
          <Typography variant="h6" sx={{ mb: 3 }}>
            {questions[currentMonster * 3 + currentQuestion].question}
          </Typography>
          {questions[currentMonster * 3 + currentQuestion].options.map((option, idx) => (
            <OptionButton
              key={idx}
              onClick={() => handleAnswer(idx)}
              isCorrect={idx === questions[currentMonster * 3 + currentQuestion].correct}
            >
              {option}
            </OptionButton>
          ))}
        </QuestionBox>
      )}

      <VictoryDialog
        open={showVictory}
        onClose={() => setShowVictory(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogContent>
          <Typography variant="h5" align="center" gutterBottom>
            Victory!
          </Typography>
          <Typography variant="body1" align="center" paragraph>
            {victoryDialogue[0].text}
          </Typography>
          <Typography variant="body1" align="center" paragraph>
            {victoryDialogue[1].text}
          </Typography>
          <StarRow>
            {[...Array(3)].map((_, i) => (
              <Star key={i} filled={i < Math.ceil((hearts / 3) * 3)}>
                ★
              </Star>
            ))}
          </StarRow>
        </DialogContent>
        <DialogActions sx={{ justifyContent: 'center', pb: 3 }}>
          <Button
            onClick={handleVictory}
            variant="contained"
            color="primary"
            disabled={isLoading}
          >
            {isLoading ? <CircularProgress size={24} /> : "Continue"}
          </Button>
        </DialogActions>
      </VictoryDialog>
    </SceneContainer>
  );
};

export default JungleLushLevel4;
