import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { keyframes } from '@mui/system';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const SceneContainer = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundImage: 'url("https://imgcdn.stablediffusionweb.com/2024/12/4/6c3aa105-23e7-41bf-b84d-b0bc2624707d.jpg")',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'relative',
  animation: `${fadeIn} 2s ease-in`,
}));

const DialogueBox = styled(Paper)(({ theme }) => ({
  position: 'absolute',
  bottom: '10%',
  left: '50%',
  transform: 'translateX(-50%)',
  padding: theme.spacing(3),
  backgroundColor: 'rgba(0, 0, 0, 0.8)',
  color: 'white',
  maxWidth: '80%',
  textAlign: 'center',
  borderRadius: '10px',
}));

const BattleButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  backgroundColor: '#f44336',
  color: 'white',
  '&:hover': {
    backgroundColor: '#d32f2f',
  },
}));

const VillageScene = ({ onComplete }) => {
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    // Show the battle button after a short delay
    const timer = setTimeout(() => {
      setShowButton(true);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <SceneContainer>
      <DialogueBox elevation={3}>
        <Typography variant="h6" gutterBottom>
          One of Dysauron's underlings is attacking this village. Face them now and prove yourself.
        </Typography>
        {showButton && (
          <BattleButton
            variant="contained"
            onClick={onComplete}
          >
            Enter Battle
          </BattleButton>
        )}
      </DialogueBox>
    </SceneContainer>
  );
};

export default VillageScene;
