import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Paper, Grid, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const JungleLushLevel3 = () => {
  const navigate = useNavigate();
  const [currentStage, setCurrentStage] = useState('intro');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [loading, setLoading] = useState(false);

  const blobbletQuestions = [
    {
      question: "Choose the correct plural form: 'There are many ___ in the pond.'",
      options: ['fishs', 'fishes', 'fish'],
      correctAnswer: 'fish'
    },
    {
      question: "Which sentence is correct?",
      options: ['The childs is reading.', 'The children are reading.', 'The childrens are reading.'],
      correctAnswer: 'The children are reading.'
    },
    {
      question: "What is the plural of 'goose'?",
      options: ['gooses', 'geese', 'goosen'],
      correctAnswer: 'geese'
    }
  ];

  const pluribogQuestions = [
    {
      question: "Choose the correct sentence:",
      options: ['The cactuses in the desert are huge.', 'The cactus are growing quickly.', 'The cacti in the desert are huge.'],
      correctAnswer: 'The cacti in the desert are huge.'
    },
    {
      question: "Which is correct?",
      options: ['I saw two sheeps in the field.', 'I saw two sheep in the field.', 'I saw two sheepes in the field.'],
      correctAnswer: 'I saw two sheep in the field.'
    },
    {
      question: "Select the correct plural: 'There were several strange ___ crawling on the log.'",
      options: ['mices', 'mice', 'mouse'],
      correctAnswer: 'mice'
    },
    {
      question: "Choose the correct sentence:",
      options: ['The data are accurate.', 'The datas is accurate.', 'The datum are accurate.'],
      correctAnswer: 'The data are accurate.'
    },
    {
      question: "What is the plural of 'phenomenon'?",
      options: ['phenomenons', 'phenomena', 'phenomenae'],
      correctAnswer: 'phenomena'
    }
  ];

  const handleAnswer = (answer) => {
    const questions = currentStage === 'blobblet' ? blobbletQuestions : pluribogQuestions;
    if (answer === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      if (currentStage === 'blobblet' && score >= 2) {
        setCurrentStage('pluribog');
        setCurrentQuestion(0);
        setScore(0);
      } else if (currentStage === 'pluribog' && score >= 4) {
        setCurrentStage('victory');
        updateProgress();
      } else {
        setCurrentStage('defeat');
      }
    }
  };

  const updateProgress = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/progress/update', { level: 3, stars: 3 }, {
        headers: { Authorization: `Bearer ${token}` }
      });
    } catch (error) {
      console.error('Error updating progress:', error);
    }
    setLoading(false);
  };

  const renderQuestion = () => {
    const questions = currentStage === 'blobblet' ? blobbletQuestions : pluribogQuestions;
    const question = questions[currentQuestion];
    return (
      <Box>
        <Typography variant="h5" gutterBottom>{question.question}</Typography>
        <Grid container spacing={2}>
          {question.options.map((option, index) => (
            <Grid item xs={12} key={index}>
              <Button
                variant="contained"
                fullWidth
                onClick={() => handleAnswer(option)}
              >
                {option}
              </Button>
            </Grid>
          ))}
        </Grid>
      </Box>
    );
  };

  const renderContent = () => {
    switch (currentStage) {
      case 'intro':
        return (
          <Box>
            <Typography variant="h4" gutterBottom>Level 3: Pluribog's Pit</Typography>
            <Typography variant="body1" paragraph>
              "We've entered Pluribog's Pit. Once, this land echoed with cheerful learning songs… now it reeks of confusion."
            </Typography>
            <Typography variant="body1" paragraph>
              "Pluribog, the Bog Beast of Broken Words, lies ahead. He is the spirit of grammatical neglect—every misused plural breathes new life into him."
            </Typography>
            <Button variant="contained" onClick={() => setCurrentStage('blobblet')}>Start Battle</Button>
          </Box>
        );
      case 'blobblet':
        return (
          <Box>
            <Typography variant="h4" gutterBottom>Blobblet's Challenge</Typography>
            {renderQuestion()}
          </Box>
        );
      case 'pluribog':
        return (
          <Box>
            <Typography variant="h4" gutterBottom>Pluribog's Challenge</Typography>
            {renderQuestion()}
          </Box>
        );
      case 'victory':
        return (
          <Box>
            <Typography variant="h4" gutterBottom>Victory!</Typography>
            <Typography variant="body1" paragraph>
              "You've defeated Pluribog and restored order to the pit!"
            </Typography>
            <Button variant="contained" onClick={() => navigate('/jungle-lush')}>Return to Map</Button>
          </Box>
        );
      case 'defeat':
        return (
          <Box>
            <Typography variant="h4" gutterBottom>Defeat</Typography>
            <Typography variant="body1" paragraph>
              "You've been overwhelmed by the confusion. Try again!"
            </Typography>
            <Button variant="contained" onClick={() => setCurrentStage('intro')}>Retry</Button>
          </Box>
        );
      default:
        return null;
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        {loading ? <CircularProgress /> : renderContent()}
      </Paper>
    </Box>
  );
};

export default JungleLushLevel3;
