import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Dialog,
  DialogContent,
  DialogActions,
  Paper,
  Grid,
  Container,
} from '@mui/material';
import { styled } from '@mui/material/styles';

const JUNGLE_BG = 'https://d2gg9evh47fn9z.cloudfront.net/1600px_COLOURBOX37132213.jpg';
const WIZARD_IMG = 'https://www.shutterstock.com/image-vector/pixel-art-wizard-character-vector-260nw-2046466115.jpg';
const USER_IMG = 'https://static.vecteezy.com/system/resources/previews/022/100/791/original/pixel-art-boy-character-png.png';

const DialogueContainer = styled(Box)(({ theme }) => ({
  position: 'fixed',
  left: 0,
  right: 0,
  bottom: 0,
  zIndex: 10,
  display: 'flex',
  justifyContent: 'center',
  pointerEvents: 'auto',
}));

const DialogueBox = styled(Paper)(({ theme }) => ({
  minWidth: 400,
  maxWidth: 700,
  margin: '0 auto',
  padding: theme.spacing(3, 4, 3, 4),
  background: '#e7c9a9',
  borderRadius: '18px 18px 0 0',
  boxShadow: '0 -4px 16px rgba(0,0,0,0.15)',
  border: '3px solid #a88b5a',
  position: 'relative',
  marginBottom: 0,
  pointerEvents: 'auto',
}));

const NamePill = styled(Box)(({ theme }) => ({
  display: 'inline-block',
  background: '#bfa074',
  color: '#3a2a13',
  fontWeight: 'bold',
  borderRadius: 16,
  padding: '4px 18px',
  fontSize: '1.1rem',
  position: 'absolute',
  top: -32,
  left: 24,
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
}));

const SpriteRow = styled(Box)(({ theme }) => ({
  width: '100%',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'flex-end',
  position: 'absolute',
  left: 0,
  right: 0,
  bottom: 120,
  zIndex: 5,
  pointerEvents: 'none',
}));

const SpriteImg = styled('img')(({ highlight }) => ({
  width: 100,
  height: 100,
  objectFit: 'contain',
  filter: highlight ? 'none' : 'grayscale(80%)',
  opacity: highlight ? 1 : 0.5,
  transition: 'filter 0.2s, opacity 0.2s',
  background: 'rgba(255,255,255,0.7)',
  borderRadius: 12,
  border: highlight ? '3px solid #a88b5a' : 'none',
}));

const BG = styled(Box)({
  minHeight: '100vh',
  width: '100vw',
  backgroundImage: `url(${JUNGLE_BG})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  position: 'fixed',
  left: 0,
  top: 0,
  zIndex: 0,
});

const CharacterName = styled(Typography)(({ theme }) => ({
  color: '#00ffaa',
  fontWeight: 'bold',
  marginBottom: theme.spacing(1),
}));

const QuestionBox = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(2),
  backgroundColor: 'rgba(0, 0, 0, 0.8)',
  color: 'white',
  borderRadius: '10px',
  border: '2px solid #ff4444',
}));

const AnswerButton = styled(Button)(({ theme, selected, correct }) => ({
  width: '100%',
  padding: theme.spacing(2),
  marginBottom: theme.spacing(1),
  backgroundColor: selected 
    ? correct 
      ? 'rgba(0, 255, 0, 0.2)' 
      : 'rgba(255, 0, 0, 0.2)'
    : 'rgba(40, 40, 100, 0.8)',
  color: 'white',
  border: `2px solid ${selected 
    ? correct 
      ? '#00ff00' 
      : '#ff0000'
    : '#00ffaa'}`,
  '&:hover': {
    backgroundColor: 'rgba(0, 255, 170, 0.2)',
  },
}));

const HealthBar = styled(Box)(({ theme }) => ({
  width: '100%',
  height: '20px',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  borderRadius: '10px',
  marginBottom: theme.spacing(2),
  overflow: 'hidden',
}));

const HealthFill = styled(Box)(({ health }) => ({
  width: `${health}%`,
  height: '100%',
  backgroundColor: health > 50 ? '#00ff00' : health > 25 ? '#ffff00' : '#ff0000',
  transition: 'width 0.3s ease-in-out',
}));

const Timer = styled(Typography)(({ theme }) => ({
  color: '#00ffaa',
  fontSize: '1.5rem',
  fontWeight: 'bold',
  textAlign: 'center',
  marginBottom: theme.spacing(2),
}));

const dialogueSequence = [
  { speaker: "Wizard", text: "This is it... the jungle where Grammowl's corruption began." },
  { speaker: "User", text: "So this is where the first scroll was stolen?" },
  { speaker: "Wizard", text: "Yes. The Scroll of Grammar lies deep within Grammowl's tower. But the path is twisted with beasts that warp language into nonsense." },
  { speaker: "Grammowl", text: "Foolish fledgling... You flutter into my grove thinking you can rewrite fate?" },
  { speaker: "User", text: "Grammowl!" },
  { speaker: "Grammowl", text: "The Scroll belongs to me now. Without it, the villagers' minds will stay broken... just how I like them." },
  { speaker: "Wizard", text: "Grammowl, your reign of confusion ends here! Chosen one, prepare yourself!" },
  { speaker: "Grammowl", text: "You'll have to get through my little pets first… if your tiny brain can handle proper punctuation!" },
  { speaker: "Commawidow", text: "Welcome to my web, sweet reader. One misplaced pause… and you'll be tangled for eternity!" },
  { speaker: "User", text: "I'm not afraid of your tricks!" },
  { speaker: "Commawidow", text: "Then let's play a little game… Answer wrong, and you'll feel the sting of silence!" },
];

const questions = [
  {
    question: "Which sentence uses commas correctly in a list?",
    options: [
      "I packed my books pencils and erasers.",
      "I packed my books, pencils and erasers.",
      "I packed my books, pencils, and erasers."
    ],
    correctAnswer: 2
  },
  {
    question: "Which greeting is correctly punctuated?",
    options: [
      "Hello John.",
      "Hello, John.",
      "Hello John,"
    ],
    correctAnswer: 1
  },
  {
    question: "Choose the sentence with correct punctuation.",
    options: [
      "I wanted to play but it started raining.",
      "I wanted to play, but it started raining.",
      "I wanted to play but, it started raining."
    ],
    correctAnswer: 1
  },
  {
    question: "Which sentence uses a comma correctly?",
    options: [
      "After dinner we went for a walk.",
      "After dinner, we went for a walk.",
      "After, dinner we went for a walk."
    ],
    correctAnswer: 1
  },
  {
    question: "Which sentence uses commas correctly?",
    options: [
      "It was a cold dark night.",
      "It was a cold, dark night.",
      "It was a cold dark, night."
    ],
    correctAnswer: 1
  }
];

const CommawidowLevel = () => {
  const navigate = useNavigate();
  const [currentDialogue, setCurrentDialogue] = useState(0);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [health, setHealth] = useState(100);
  const [timeLeft, setTimeLeft] = useState(60);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [victory, setVictory] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);

  useEffect(() => {
    if (currentDialogue < dialogueSequence.length) {
      const timer = setTimeout(() => {
        setCurrentDialogue(prev => prev + 1);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [currentDialogue]);

  useEffect(() => {
    if (timeLeft > 0 && !gameOver && !victory) {
      const timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else if (timeLeft === 0) {
      setGameOver(true);
    }
  }, [timeLeft, gameOver, victory]);

  const handleAnswer = (index) => {
    setSelectedAnswer(index);
    setShowResult(true);

    if (index === questions[currentQuestion].correctAnswer) {
      if (currentQuestion === questions.length - 1) {
        setVictory(true);
      } else {
        setTimeout(() => {
          setCurrentQuestion(prev => prev + 1);
          setSelectedAnswer(null);
          setShowResult(false);
        }, 1500);
      }
    } else {
      setHealth(prev => Math.max(0, prev - 20));
      if (health <= 20) {
        setGameOver(true);
      }
    }
  };

  const handleContinue = () => {
    if (victory) {
      navigate('/jungle-lush');
    } else if (gameOver) {
      navigate('/jungle-lush');
    }
  };

  // Dialogue click handler
  const handleDialogueClick = () => {
    if (currentDialogue < dialogueSequence.length - 1) {
      setCurrentDialogue(currentDialogue + 1);
    } else {
      setShowQuiz(true);
    }
  };

  // Render dialogue sequence
  if (!showQuiz) {
    const speaker = dialogueSequence[currentDialogue].speaker;
    return (
      <BG>
        <SpriteRow>
          <SpriteImg src={WIZARD_IMG} highlight={speaker === 'Wizard'} style={{marginLeft: 40}} />
          <SpriteImg src={USER_IMG} highlight={speaker === 'User'} style={{marginRight: 40}} />
        </SpriteRow>
        <DialogueContainer>
          <DialogueBox elevation={6} onClick={handleDialogueClick} style={{cursor: 'pointer'}}>
            <NamePill>{speaker}</NamePill>
            <Typography variant="body1" sx={{mt: 3, fontSize: '1.2rem', color: '#3a2a13'}}>
              {dialogueSequence[currentDialogue].text}
            </Typography>
            <Typography variant="caption" sx={{position: 'absolute', right: 24, bottom: 8, color: '#a88b5a'}}>
              Click to continue
            </Typography>
          </DialogueBox>
        </DialogueContainer>
      </BG>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
            <HealthBar>
              <HealthFill health={health} />
            </HealthBar>
            <Timer>{timeLeft}s</Timer>
          </Box>
        </Grid>
        
        <Grid item xs={12}>
          <QuestionBox>
            <Typography variant="h5" gutterBottom>
              {questions[currentQuestion].question}
            </Typography>
            <Grid container spacing={2}>
              {questions[currentQuestion].options.map((option, index) => (
                <Grid item xs={12} key={index}>
                  <AnswerButton
                    variant="contained"
                    onClick={() => handleAnswer(index)}
                    disabled={showResult}
                    selected={selectedAnswer === index}
                    correct={index === questions[currentQuestion].correctAnswer}
                  >
                    {option}
                  </AnswerButton>
                </Grid>
              ))}
            </Grid>
          </QuestionBox>
        </Grid>
      </Grid>

      <Dialog open={gameOver || victory} onClose={handleContinue}>
        <DialogContent>
          <Typography variant="h5" align="center">
            {victory ? "Victory! You've defeated Commawidow!" : "Game Over! Try again!"}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleContinue} color="primary">
            Continue
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default CommawidowLevel;
