import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import { styled } from '@mui/material/styles';
import DreamIntro from './scenes/DreamIntro';
import WizardScene from './scenes/WizardScene';
import VillageScene from './scenes/VillageScene';
import TutorialBattle from './battle/TutorialBattle';
import VictoryScene from './scenes/VictoryScene';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const FullScreenContainer = styled(Box)(({ theme }) => ({
  width: '100vw',
  height: '100vh',
  position: 'fixed',
  top: 0,
  left: 0,
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundColor: 'black',
  color: 'white',
  overflow: 'hidden',
}));

const QuitButton = styled(Button)(({ theme }) => ({
  position: 'absolute',
  top: '20px',
  right: '20px',
  backgroundColor: 'rgba(255, 0, 0, 0.9)',
  color: 'white',
  '&:hover': {
    backgroundColor: 'rgba(255, 0, 0, 1)',
  },
  zIndex: 2000,
  padding: '8px 16px',
  fontSize: '1rem',
  fontWeight: 'bold',
  boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
  '&:hover': {
    backgroundColor: 'rgba(255, 0, 0, 1)',
    transform: 'scale(1.05)',
  },
  transition: 'all 0.3s ease',
}));

const ReturnToMapButton = styled(Button)(({ theme }) => ({
  position: 'absolute',
  top: '20px',
  right: '180px',
  backgroundColor: 'rgba(76, 175, 80, 0.9)',
  color: 'white',
  '&:hover': {
    backgroundColor: 'rgba(76, 175, 80, 1)',
  },
  zIndex: 2000,
  padding: '8px 16px',
  fontSize: '1rem',
  fontWeight: 'bold',
  boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
  '&:hover': {
    backgroundColor: 'rgba(76, 175, 80, 1)',
    transform: 'scale(1.05)',
  },
  transition: 'all 0.3s ease',
}));

const TutorialSequence = () => {
  const [currentScene, setCurrentScene] = useState('dream');
  const [battleResults, setBattleResults] = useState(null);
  const [showQuitDialog, setShowQuitDialog] = useState(false);
  const [isNewUser, setIsNewUser] = useState(false);
  const navigate = useNavigate();

  // Check if user is new (no adventure profile or tutorial not completed)
  useEffect(() => {
    const checkUserStatus = async () => {
      try {
        const userStr = localStorage.getItem('user');
        if (!userStr) {
          console.log('No user found in localStorage');
          setIsNewUser(true);
          return;
        }
        const user = JSON.parse(userStr);
        console.log('User from localStorage:', user);
        // Try to fetch adventure profile
        const res = await axios.get(`http://localhost:8081/api/adventure-profile?userId=${user.id}`, { withCredentials: true });
        console.log('Adventure profile response:', res.data);
        // Only treat as returning user if tutorialCompleted (or similar) is true
        const isCompleted = res.data && (res.data.tutorialCompleted || res.data.tutorial_completed || res.data.completedTutorial);
        console.log('Is tutorial completed?', isCompleted);
        setIsNewUser(!isCompleted);
      } catch (err) {
        console.error('Error fetching adventure profile:', err);
        // If error (profile not found), treat as new user
        setIsNewUser(true);
      }
    };
    checkUserStatus();
  }, []);

  // Add debug log for isNewUser state
  useEffect(() => {
    console.log('isNewUser state changed:', isNewUser);
  }, [isNewUser]);

  const handleSceneComplete = (nextScene) => {
    setCurrentScene(nextScene);
  };

  const handleBattleComplete = (results) => {
    setBattleResults(results);
    setCurrentScene('victory');
  };

  const handleQuitClick = () => {
    setShowQuitDialog(true);
  };

  const handleQuitCancel = () => {
    setShowQuitDialog(false);
  };

  const renderScene = () => {
    switch (currentScene) {
      case 'dream':
        return <DreamIntro onComplete={() => handleSceneComplete('wizard')} />;
      case 'wizard':
        return <WizardScene onComplete={() => handleSceneComplete('village')} />;
      case 'village':
        return <VillageScene onComplete={() => handleSceneComplete('battle')} />;
      case 'battle':
        return <TutorialBattle onComplete={handleBattleComplete} />;
      case 'victory':
        return <VictoryScene results={battleResults} />;
      default:
        return null;
    }
  };

  return (
    <FullScreenContainer>
      <div style={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        padding: '20px', 
        display: 'flex', 
        justifyContent: 'flex-end', 
        gap: '20px',
        zIndex: 2000,
        pointerEvents: 'auto'
      }}>
        {!isNewUser && (
          <ReturnToMapButton
            variant="contained"
            onClick={(e) => {
              e.stopPropagation();
              navigate('/map');
            }}
          >
            Return to Map
          </ReturnToMapButton>
        )}
        <QuitButton
          variant="contained"
          onClick={(e) => {
            e.stopPropagation();
            handleQuitClick();
          }}
        >
          {isNewUser ? 'Quit Tutorial' : 'Exit Tutorial'}
        </QuitButton>
      </div>

      <div style={{ pointerEvents: 'auto' }}>
        {!showQuitDialog && renderScene()}
      </div>

      <Dialog
        open={showQuitDialog}
        onClose={handleQuitCancel}
        PaperProps={{
          style: {
            backgroundColor: '#1a1a1a',
            color: 'white',
            minWidth: '300px',
          },
        }}
      >
        <DialogTitle>Quit Tutorial?</DialogTitle>
        <DialogContent>
          <Typography>
            {isNewUser 
              ? "Are you sure you want to quit the tutorial? Your progress will be lost."
              : "Are you sure you want to exit the tutorial?"}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleQuitCancel} color="primary" variant="contained">
            Continue Tutorial
          </Button>
          {isNewUser ? (
            <Button onClick={() => navigate('/home')} color="error" variant="contained">
              Quit Tutorial
            </Button>
          ) : (
            <>
              <Button onClick={() => navigate('/home')} color="error" variant="contained">
                Return to Home
              </Button>
              <Button onClick={() => navigate('/map')} color="secondary" variant="contained">
                Return to Map
              </Button>
            </>
          )}
        </DialogActions>
      </Dialog>
    </FullScreenContainer>
  );
};

export default TutorialSequence;
