import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';

function HelloUser({ setIsAuthenticated }) {
  const handleLogout = () => {
    localStorage.removeItem('token');  // Clear the token on logout
    setIsAuthenticated(false);          // Update auth status
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>HELLO USER</h1>
      <p>Welcome to Vocabia!</p>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  const updateAuthStatus = () => {
    setIsAuthenticated(!!localStorage.getItem('token'));
  };

  useEffect(() => {
    window.addEventListener('storage', updateAuthStatus);
    return () => window.removeEventListener('storage', updateAuthStatus);
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        {/* Default "/" shows login page */}
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/hello" replace /> : <Login setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* Register route */}
        <Route
          path="/register"
          element={isAuthenticated ? <Navigate to="/hello" replace /> : <Register setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* HelloUser page - protected */}
        <Route
          path="/hello"
          element={isAuthenticated ? <HelloUser setIsAuthenticated={setIsAuthenticated} /> : <Navigate to="/" replace />}
        />

        {/* Redirect any unknown routes to login */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
