import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Pages/Login';
import Register from './Pages/Register';

function HelloUser() {
  return (
    <div style={{ padding: 20 }}>
      <h1>HELLO USER</h1>
      <p>Welcome to Vocabia!</p>
    </div>
  );
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  const updateAuthStatus = () => {
    setIsAuthenticated(!!localStorage.getItem('token'));
  };

  useEffect(() => {
    window.addEventListener('storage', updateAuthStatus);
    return () => window.removeEventListener('storage', updateAuthStatus);
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        {/* Always show Login at "/" */}
        <Route path="/" element={<Login setIsAuthenticated={setIsAuthenticated} />} />

        {/* Login and Register routes redirect if authenticated */}
        <Route
          path="/login"
          element={isAuthenticated ? <Navigate to="/hello" replace /> : <Login setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/register"
          element={isAuthenticated ? <Navigate to="/hello" replace /> : <Register setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* Protected HelloUser page */}
        <Route
          path="/hello"
          element={isAuthenticated ? <HelloUser /> : <Navigate to="/" replace />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
